ack", true )addEventHandler("loginCallback", getRootElement(), loginCallback)function playerResourceStart()	local mtaVersion = getVersion()	triggerServerEvent("onPlayerReady", localPlayer, mtaVersion.type)endaddEventHandler("onClientResourceStart", getResourceRootElement(getThisResource()), playerResourceStart)